pragma solidity 0.6.6;
pragma experimental ABIEncoderV2;

import '@kleros/erc-792/contracts/IArbitrator.sol';
import '@kleros/ethereum-libraries/contracts/CappedMath.sol';
import '@openzeppelin/contracts-ethereum-package/contracts/Initializable.sol';
import './IClaims.sol';
import './IClaimableMarket.sol';
import './oracle/IOracle.sol';
import './dispute-resolver/IDisputeResolver.sol';

/**
 *  @title KlerosConnector
 *  @dev This contract acts a connector between Unslashed and Kleros court.
 *  NOTE: This contract trusts that the Arbitrator is honest and will not reenter or modify its costs during a call.
 *  The arbitrator must support appeal period.
 *  The contract also trusts that Claims and Market contracts are honest and won't reenter.
 */
contract KlerosConnector is Initializable, IDisputeResolver {
    using CappedMath for uint256;

    /* Constants */

    uint256 private constant RULING_OPTIONS = 2; // The amount of non 0 choices the arbitrator can give.
    uint256 private constant MULTIPLIER_DIVISOR = 10000; // Divisor parameter for multipliers.

    /* Enums */

    enum Party {
        None, // The default party, when there is no challenger or claimant. Also used for inconclusive ruling.
        Claimant, // The party that makes a claim.
        Challenger // The party that challenges a claim.
    }

    enum Status {
        None, // The claim was not created.
        Created, // The claim was created and added to the contract.
        Disputed, // The claim has been challenged, and a dispute was created in the arbitrator contract.
        Readjustable, // The claimed amount can be readjusted.
        Resolved // The claim has been resolved.
    }

    /* Structs */

    struct ClaimData {
        Status status; // Status of the claim.
        address payable claimant; // Address that made the claim.
        address payable challenger; // Address that challenged the claim, if any.
        uint64 lastActionTime; // Records the timestamps of timeout-related actions.
        address marketAddress; // Address of the market that will pay the claimed amount.
        bool groupWasChanged; // True if the default group of the claim was changed. Note that this flag is also active when the claim is added to the existing group right away.
        bool amountReadjusted; // True if the amount of this claim was readjusted.
        uint64 metaEvidenceID; // The index of the relevant metaevidence string.
        uint256 amount; // Amount of tokens the claimant wants to get compensated for.
        uint256 groupClaimId; // The id of the group claim in Claims contract.
        uint256 userClaimId; // The id of the user claim in Claims contract.
        uint256 localDisputeID; // The index of the related dispute in the array of disputes in this contract. Note that in case of challenge of the readjusted claim the old ID is overwritten.
    }

    struct DisputeData {
        uint256 externalDisputeID; // The ID of the dispute created in arbitrator contract.
        uint256 claimID; // The ID of the disputed claim.
        Party ruling; // Ruling given by the arbitrator.
        bool resolved; // True if the dispute has been resolved.
        Round[] rounds; // Stores the data of funding rounds.
    }

    // Arrays with 3 elements map with the Party enum for better readability:
    // - 0: is unused, matches `Party.None`.
    // - 1: for `Party.Claimant`.
    // - 2: for `Party.Challenger`.
    struct Round {
        uint256[3] paidFees; // Tracks the fees paid by each side in this round.
        Party sideFunded; // Stores the side that successfully paid the appeal fees in the latest round. Note that if both sides have paid a new round is created. Also note that it's not used in the 0 round because successful payments are determined by the status in this case.
        uint256 feeRewards; // Sum of reimbursable fees and stake rewards available to the parties that made contributions to the side that ultimately wins a dispute.
        mapping(address => uint256[3]) contributions; // Maps contributors to their contributions for each side.
    }

    /* Storage */

    IArbitrator public arbitrator; // The arbitrator contract.
    uint64 public metaEvidenceUpdates; // The meta evidence to use in disputes.
    bytes public arbitratorExtraData; // Extra data for the arbitrator.
    IClaims public claims; // Address of the contract Claims.sol.

    address public governor; // The governor of this contract.
    uint256 public nbClaims; // Total number of created claims.
    uint256 public challengeTimeout; // Time a challenger has to challenge the claim.
    uint256 public readjustmentTimeout; // Time during which the claim can be readjusted.
    uint256 public minimumDeposit; // The minimum deposit the claimant has to pay if the claimed amount is too low.

    // Multipliers are in basis points.
    uint256 public claimantMultiplier; // Multiplier for calculating the deposit the claimant must pay in order to create a claim.
    uint256 public winnerStakeMultiplier; // Multiplier for calculating the fee stake paid by the party that won the previous round. Also applies to the rounds that resulted in draw.
    uint256 public loserStakeMultiplier; // Multiplier for calculating the fee stake paid by the party that lost the previous round.

    DisputeData[] public disputes; // Stores all the dispute-related info. Note that the new element is pushed when the new claim is made and not when the dispute is actually created in Arbitrator contract.

    mapping(uint256 => ClaimData) public claimsData; // Stores the data of all created claims. claimsData[claimID].
    mapping(address => address) public tokenToOracle; // The address of the Uniswap oracle contract that is used for a specific token. tokenToOracle[tokenAddress].
    mapping(uint256 => uint256) public override externalIDtoLocalID; // Maps external dispute ids to local dispute ids.
    mapping(bytes32 => bool) public claimCreated; // Stores the hashed info of the claim in order to catch duplicates. claimCreated[hash].

    /* Events */

    /**
     *  @dev Emitted when a new claim is created.
     *  @param _claimID The ID of the claim.
     *  @param _claimant The address that made the claim.
     */
    event NewClaim(uint256 indexed _claimID, address indexed _claimant);

    /**
     *  @dev Emitted when a new claim is created. 
     *  For finding Kleros claimID with unslashed group and user id-s.
     *  @param _marketAddress The address of the related market.
     *  @param _groupClaimId Claims contract groupClaimId.
     *  @param _userClaimId Claims contract userClaimId.
     *  @param _claimID The ID of the claim.
     */
    event NewClaimID(address indexed _marketAddress, uint256 indexed _groupClaimId, uint256 indexed _userClaimId, uint256 _claimID);

    /**
     *  @dev Emitted when a claim changes its group.
     *  @param _claimID The ID of the claim.
     *  @param _newGroup The index of the new group in Claims contract.
     */
    event GroupClaimChanged(uint256 indexed _claimID, uint256 indexed _newGroup);

    /**
     *  @dev Emitted when a claim is challenged.
     *  @param _claimID The ID of the claim.
     *  @param _challenger The address of the challenger.
     *  @param _localDisputeID The index of the dispute in the array of disputes in this contract.
     *  @param _readjusted Whether the claim is readjusted or not.
     */
    event ClaimChallenged(uint256 indexed _claimID, address indexed _challenger, uint256 indexed _localDisputeID, bool _readjusted);

    /**
     *  @dev Emitted when a claim's amount is readjusted.
     *  @param _claimID The ID of the claim.
     */
    event ClaimReadjusted(uint256 indexed _claimID);

    /**
     *  @dev Emitted when a claim is resolved.
     *  @param _claimID The ID of the claim.
     */
    event ClaimResolved(uint256 indexed _claimID);

    /* Modifiers */

    modifier onlyGovernor {require(msg.sender == governor, 'The caller must be the governor.'); _;}

    /** @dev Constructor.
     *  @param _arbitrator The arbitrator of the contract.
     *  @param _arbitratorExtraData Extra data for the arbitrator.
     *  @param _claims The address of Claims.sol contract.
     *  @param _metaEvidence The URI of the meta evidence object.
     *  @param _challengeTimeout Time in seconds during which it is possible to challenge a claim.
     *  @param _readjustmentTimeout Time in seconds during which it is possible to readjust the amount of the claim.
     *  @param _minimumDeposit The minimum deposit the claimant has to pay if the claimed amount is too low.
     *  @param _claimantMultiplier Multiplier for calculating the deposit of the claimant.
     *  @param _winnerStakeMultiplier Multiplier of the arbitration cost that the winner has to pay as fee stake for a round in basis points. Also applies in case of a draw.
     *  @param _loserStakeMultiplier Multiplier of the arbitration cost that the loser has to pay as fee stake for a round in basis points.
     */
    function initialize(
        IArbitrator _arbitrator,
        bytes calldata _arbitratorExtraData,
        IClaims _claims,
        string calldata _metaEvidence,
        uint256 _challengeTimeout,
        uint256 _readjustmentTimeout,
        uint256 _minimumDeposit,
        uint256 _claimantMultiplier,
        uint256 _winnerStakeMultiplier,
        uint256 _loserStakeMultiplier
    ) external initializer {
        emit MetaEvidence(0, _metaEvidence);

        arbitrator = _arbitrator;
        arbitratorExtraData = _arbitratorExtraData;
        claims = _claims;
        governor = msg.sender;
        challengeTimeout = _challengeTimeout;
        readjustmentTimeout = _readjustmentTimeout;
        minimumDeposit = _minimumDeposit;
        claimantMultiplier = _claimantMultiplier;
        winnerStakeMultiplier = _winnerStakeMultiplier;
        loserStakeMultiplier = _loserStakeMultiplier;
    }

    /* External and Public */

    // ************************ //
    // *      Governance      * //
    // ************************ //

    /** @dev Change the duration of the challenge period.
     *  @param _challengeTimeout The new duration of the challenge period.
     */
    function changeChallengeTimeout(uint256 _challengeTimeout) external onlyGovernor {
        challengeTimeout = _challengeTimeout;
    }

    /** @dev Change the duration of the readjustment period.
     *  @param _readjustmentTimeout The new duration of the readjustment period.
     */
    function changeReadjustmentTimeout(uint256 _readjustmentTimeout) external onlyGovernor {
        readjustmentTimeout = _readjustmentTimeout;
    }

    /** @dev Change the minimum deposit the claimant has to pay when the claimed value is too low.
     *  @param _minimumDeposit The new minimum deposit for claimant.
     */
    function changeMinimumDeposit(uint256 _minimumDeposit) external onlyGovernor {
        minimumDeposit = _minimumDeposit;
    }

    /** @dev Change the proportion of the requested amount that must be paid by the claimant in order to create a new claim.
     *  @param _claimantMultiplier The new multiplier for calculating the claimant's deposit. In basis points.
     */
    function changeClaimantMultiplier(uint256 _claimantMultiplier) external onlyGovernor {
        claimantMultiplier = _claimantMultiplier;
    }

    /** @dev Change the proportion of arbitration fees that must be paid as fee stake by the winner of the previous round. Also applies in case of a draw.
     *  @param _winnerStakeMultiplier The new multiplier of arbitration fees that must be paid as fee stake. In basis points.
     */
    function changeWinnerStakeMultiplier(uint256 _winnerStakeMultiplier) external onlyGovernor {
        winnerStakeMultiplier = _winnerStakeMultiplier;
    }

    /** @dev Change the proportion of arbitration fees that must be paid as fee stake by the party that lost the previous round.
     *  @param _loserStakeMultiplier The new multiplier of arbitration fees that must be paid as fee stake. In basis points.
     */
    function changeLoserStakeMultiplier(uint256 _loserStakeMultiplier) external onlyGovernor {
        loserStakeMultiplier = _loserStakeMultiplier;
    }

    /** @dev Change the governor of the contract.
     *  @param _governor The address of the new governor.
     */
    function changeGovernor(address _governor) external onlyGovernor {
        governor = _governor;
    }

    /** @dev Update the meta evidence used for disputes.
     *  @param _metaEvidence URI of the new meta evidence.
     */
    function changeMetaEvidence(string calldata _metaEvidence) external onlyGovernor {
        metaEvidenceUpdates++;
        emit MetaEvidence(metaEvidenceUpdates, _metaEvidence);
    }

    /** @dev Map the address of the oracle to a specific token.
     *  @param _token The address of the token.
     *  @param _oracle The address of the oracle to convert tokens into ETH.
     */
    function setOracle(address _token, address _oracle) external onlyGovernor {
        tokenToOracle[_token] = _oracle;
    }

    /** @dev Set the address of the Claims contract.
     *  @param _claims The address of the Claims contract.
     */
    function setClaimsContract(IClaims _claims) external onlyGovernor {
        claims = _claims;
    }

    // ********************** //
    // *       Claims       * //
    // ********************** //

    /** @dev Make a new claim in Connector and Claims contracts.
     *  Requires the fraction of the claimed amount as a deposit. Accepts enough ETH to cover the deposit, reimburses the rest.
     *  @param _marketAddress The address of the market contract claimed
     *  @param _amount The claimed amount.
     *  @param _dappCover True if the claim is covered by the dapp.
     *  @param _evidences A list of links to evidence using its URI. Ignored if not provided. If claim is first of the incident, the incident url has to be the first element
     *  @param _incidentGroupClaimId id connecting all the claims in the same incident, groupClaimId of the first claim in the incident if == uint256(-1) it means that this claim is the first in the incident
     */
    function makeNewClaim(address _marketAddress, uint256 _amount, bool _dappCover, string[] calldata _evidences, uint256 _incidentGroupClaimId) external payable {
        // Calculate the required deposit based on the market token and the claimed amount.
        uint256 claimantDeposit = getClaimantDeposit(_marketAddress, _amount);
        require(msg.value >= claimantDeposit, 'Should pay the full deposit.');
        uint256 claimID = nbClaims;
        uint256 localDisputeID = disputes.length;

        ClaimData storage claimData = claimsData[claimID];
        claimData.metaEvidenceID = metaEvidenceUpdates;
        claimData.status = Status.Created;
        claimData.claimant = msg.sender;
        claimData.marketAddress = _marketAddress;
        claimData.amount = _amount;

        IClaims claimsContract = claims;
        uint256 groupClaimId;
        if (_incidentGroupClaimId == uint256(-1)) {
            groupClaimId = claimsContract.addIncidentGroupClaim(_marketAddress, msg.sender, _amount, _dappCover, _evidences[0]);
        } else {
            groupClaimId = claimsContract.addGroupClaim(_marketAddress, msg.sender, _amount, _dappCover,  _incidentGroupClaimId);
        }
        claimData.groupClaimId = groupClaimId;
        claimData.lastActionTime = uint64(now);
        claimData.localDisputeID = localDisputeID;

        claimCreated[keccak256(abi.encodePacked(claimsContract, _marketAddress, groupClaimId, msg.sender))] = true;

        {
            DisputeData storage disputeData = disputes.push();
            disputeData.claimID = claimID;
            Round storage round = disputeData.rounds.push();
            contribute(round, Party.Claimant, msg.value, claimantDeposit);
        }
        emit NewClaim(claimID, msg.sender);
        emit NewClaimID(_marketAddress, groupClaimId, 0, claimID);

        IArbitrator claimArbitrator = arbitrator;
        // We dont emit an event for the incident. It is done in Claims.sol
        uint256 i = _incidentGroupClaimId==uint256(-1) ? 1 : 0;
        for (; i < _evidences.length; i++) {
            emit Evidence(claimArbitrator, localDisputeID, msg.sender, _evidences[i]);
        }

        nbClaims = claimID + 1;
    }

    /** @dev Make a new claim and join an existing claim group.
     *  Requires the fraction of the claimed amount as a deposit. Accepts enough ETH to cover the deposit, reimburses the rest.
     *  @param _marketAddress The address of the market contract.
     *  @param _groupClaimId The ID of the group to join in Claims contract.
     *  @param _amount Claimed amount.
     *  @param _evidences A list of links to evidences using its URIs. Ignored if not provided.
     */
    function makeNewClaimAndJoinGroup(address _marketAddress, uint256 _groupClaimId, uint256 _amount, string[] calldata _evidences) external payable {
        uint256 claimantDeposit = getClaimantDeposit(_marketAddress, _amount);
        IClaims claimsContract = claims;
        bytes32 claimHash = keccak256(abi.encodePacked(claimsContract, _marketAddress, _groupClaimId, msg.sender));
        require(!claimCreated[claimHash], 'Can not make a duplicate claim.');
        require(msg.value >= claimantDeposit, 'Should pay the full deposit.');

        uint256 claimID = nbClaims;
        uint256 localDisputeID = disputes.length;

        ClaimData storage claimData = claimsData[claimID];
        claimData.metaEvidenceID = metaEvidenceUpdates;
        claimData.status = Status.Created;
        claimData.claimant = msg.sender;
        claimData.marketAddress = _marketAddress;
        claimData.amount = _amount;
        claimData.groupClaimId = _groupClaimId;
        claimData.lastActionTime = uint64(now);
        claimData.groupWasChanged = true;
        claimData.localDisputeID = localDisputeID;
        claimCreated[claimHash] = true;

        uint256 userClaimId = claimsContract.joinGroupClaim(_marketAddress, _groupClaimId, msg.sender, _amount);
        claimData.userClaimId = userClaimId;

        {
            DisputeData storage disputeData = disputes.push();
            disputeData.claimID = claimID;
            Round storage round = disputeData.rounds.push();
            contribute(round, Party.Claimant, msg.value, claimantDeposit);
        }        
        emit NewClaim(claimID, msg.sender);
        emit NewClaimID(_marketAddress, _groupClaimId, userClaimId, claimID);

        IArbitrator claimArbitrator = arbitrator;
        for (uint256 i = 0; i < _evidences.length; i++) {
            emit Evidence(claimArbitrator, localDisputeID, msg.sender, _evidences[i]);
        }
        nbClaims = claimID + 1;
    }

    /** @dev Join a new claim group. Can only be called once per claim.
     *  @param _claimID  The ID of the claim for which group is to be changed.
     *  @param _groupClaimId The ID of the new claim group.
     */
    function joinGroupClaim(uint256 _claimID, uint256 _groupClaimId) external {
        ClaimData storage claimData = claimsData[_claimID];
        IClaims claimsContract = claims;
        address marketAddress = claimData.marketAddress;

        bytes32 claimHash = keccak256(abi.encodePacked(claimsContract, marketAddress, _groupClaimId, msg.sender));
        require(claimData.status == Status.Created, 'Status should be Created.');
        require(claimData.claimant == msg.sender, 'Only claimant can execute this.');
        require(!claimCreated[claimHash], 'Can not make a duplicate claim.');
        require(!claimData.groupWasChanged, 'Group has already been changed.');

        claimData.groupWasChanged = true;
        claimCreated[claimHash] = true;

        // Refuse old one.
        claimsContract.decideUserClaim(marketAddress, claimData.groupClaimId, claimData.userClaimId, false);

        claimData.groupClaimId = _groupClaimId;

        claimData.userClaimId = claimsContract.joinGroupClaim(marketAddress, _groupClaimId, msg.sender, claimData.amount);
        emit GroupClaimChanged(_claimID, _groupClaimId);
        emit NewClaimID(marketAddress, _groupClaimId, claimData.userClaimId, _claimID);
    }

    /** @dev Challenge the claim and create a dispute.
     *  Requires the value of the arbitration cost as a deposit. Accepts enough ETH to cover the deposit, reimburses the rest.
     *  @param _claimID The ID of the claim to challenge.
     *  @param _evidences A list of links to evidences using its URIs. Ignored if not provided.
     */
    function challengeClaim(uint256 _claimID, string[] calldata _evidences) external payable {
        ClaimData storage claimData = claimsData[_claimID];
        IArbitrator claimArbitrator = arbitrator;

        uint256 arbitrationCost = claimArbitrator.arbitrationCost(arbitratorExtraData);
        require(claimData.status == Status.Created, 'Status must be Created.');
        require(now - claimData.lastActionTime <= challengeTimeout, 'Time to challenge has passed.');
        require(msg.value >= arbitrationCost, 'Should pay the full deposit.');

        claimData.status = Status.Disputed;
        claimData.challenger = msg.sender;
        DisputeData storage disputeData = disputes[claimData.localDisputeID];
        // Create a dispute in arbitrator contract.
        uint256 externalDisputeID = claimArbitrator.createDispute{value: arbitrationCost}(RULING_OPTIONS,  arbitratorExtraData);
        uint256 localDisputeID = claimData.localDisputeID;
        disputeData.externalDisputeID = externalDisputeID;
        externalIDtoLocalID[externalDisputeID] = localDisputeID;

        Round storage round = disputeData.rounds[disputeData.rounds.length - 1];
        contribute(round, Party.Challenger, msg.value, arbitrationCost);
        round.feeRewards = round.feeRewards.subCap(arbitrationCost);
        disputeData.rounds.push();

        emit ClaimChallenged(_claimID, msg.sender, localDisputeID, claimData.amountReadjusted);
        emit Dispute(claimArbitrator, externalDisputeID, claimData.metaEvidenceID, localDisputeID);

        for (uint256 i = 0; i < _evidences.length; i++) {
            emit Evidence(claimArbitrator, localDisputeID, msg.sender, _evidences[i]);
        }
    }

    /** @dev Accept the claim that wasn't challenged during the challenge period.
     *  @param _claimID The ID of the claim.
     */
    function acceptClaim(uint256 _claimID) external {
        ClaimData storage claimData = claimsData[_claimID];
        require(claimData.status == Status.Created, 'Status should be Created.');
        require(now - claimData.lastActionTime > challengeTimeout, 'Time to challenge has not passed');

        claimData.status = Status.Resolved;

        if (claimData.amountReadjusted)
            claims.decideUserClaimChangeAmount(claimData.marketAddress, claimData.groupClaimId, claimData.userClaimId, true, claimData.amount);
        else
            claims.decideUserClaim(claimData.marketAddress, claimData.groupClaimId, claimData.userClaimId, true);

        emit ClaimResolved(_claimID);

        withdrawFeesAndRewards(claimData.localDisputeID, claimData.claimant, 0, uint(Party.Claimant));
    }

    /** @dev Refuse the claim that wasn't readjusted during the readjustment period.
     *  @param _claimID The ID of the claim.
     */
    function refuseClaim(uint256 _claimID) external {
        ClaimData storage claimData = claimsData[_claimID];
        require(claimData.status == Status.Readjustable, 'Status should be Readjustable.');
        claimData.status = Status.Resolved;

        require(now - claimData.lastActionTime > readjustmentTimeout, 'Time to readjust has not passed');
        claims.decideUserClaim(claimData.marketAddress, claimData.groupClaimId, claimData.userClaimId, false);

        emit ClaimResolved(_claimID);
    }

    /** @dev Change the claimed amount. Resets the claim to its initial state and makes it open for a new challenge.
     *  Can only be called once per claim and after the claim has been declined.
     *  @param _claimID The ID of the claim which amount to readjust.
     *  @param _newAmount The new amount to claim.
     *  @param _evidences A list of links to evidences using its URIs. Ignored if not provided.
     */
    function readjustClaim(uint256 _claimID, uint256 _newAmount, string[] calldata _evidences) external payable {
        ClaimData storage claimData = claimsData[_claimID];
        require(claimData.status == Status.Readjustable, 'Status must be Readjustable.');
        require(claimData.claimant == msg.sender, 'Only claimant can execute this.');
        require(_newAmount != 0 && _newAmount < claimData.amount, 'Wrong amount.');
        require(now - claimData.lastActionTime <= readjustmentTimeout, 'Time to readjust has passed.');

        uint256 claimantDeposit = getClaimantDeposit(claimData.marketAddress, _newAmount);
        require(msg.value >= claimantDeposit, 'Should pay the full deposit.');

        claimData.status = Status.Created;
        claimData.amount = _newAmount;
        claimData.lastActionTime = uint64(now);
        claimData.amountReadjusted = true;
        uint256 localDisputeID = disputes.length;
        claimData.localDisputeID = localDisputeID;

        DisputeData storage disputeData = disputes.push();
        disputeData.claimID = _claimID;
        Round storage round = disputeData.rounds.push();
        contribute(round, Party.Claimant, msg.value, claimantDeposit);

        emit ClaimReadjusted(_claimID);

        IArbitrator claimArbitrator = arbitrator;
        for (uint256 i = 0; i < _evidences.length; i++) {
            emit Evidence(claimArbitrator, localDisputeID, msg.sender, _evidences[i]);
        }
    }

    /** @dev Take up to the total amount required to fund a side of an appeal. Reimburse the rest. Create an appeal if both sides are fully funded.
     *  @param _localDisputeID The index of the related dispute in the array of disputes.
     *  @param _ruling The ruling to contribute to.
     *  @return fullyFunded Whether the side of an appeal was fully funded or not.
     */
    function fundAppeal(uint256 _localDisputeID, uint256 _ruling) external payable override returns (bool fullyFunded) {
        require(_ruling == 1 || _ruling == 2, 'Invalid ruling.');
        DisputeData storage disputeData = disputes[_localDisputeID];
        ClaimData storage claimData = claimsData[disputeData.claimID];
        require(claimData.status == Status.Disputed, 'Status must be Disputed.');
        IArbitrator claimArbitrator = arbitrator;

        (uint256 appealPeriodStart, uint256 appealPeriodEnd) = claimArbitrator.appealPeriod(disputeData.externalDisputeID);
        require(now >= appealPeriodStart && now < appealPeriodEnd, 'Appeal period has passed.');

        uint256 multiplier;
        {
            uint256 winner = claimArbitrator.currentRuling(disputeData.externalDisputeID);
            if (winner==_ruling || winner==0) {
                multiplier = winnerStakeMultiplier;
            } else {
                multiplier = loserStakeMultiplier;
                require(now < (appealPeriodStart+appealPeriodEnd)/2, 'Time for losing party has passed');
            }
        }

        Round storage round = disputeData.rounds[disputeData.rounds.length - 1];
        require(_ruling != uint256(round.sideFunded), 'Appeal fee has already been paid');
        uint256 appealCost = claimArbitrator.appealCost(disputeData.externalDisputeID, arbitratorExtraData);
        uint256 totalCost = appealCost.addCap((appealCost.mulCap(multiplier)) / MULTIPLIER_DIVISOR);
        uint256 contribution = contribute(round, Party(_ruling), msg.value, totalCost);

        emit Contribution(_localDisputeID, disputeData.rounds.length - 1, _ruling, msg.sender, contribution);

        if (round.paidFees[_ruling] >= totalCost) {
            emit RulingFunded(_localDisputeID, disputeData.rounds.length - 1, _ruling);
            if (round.sideFunded == Party.None) {
                round.sideFunded = Party(_ruling);
            } else {
                // Both sides are fully funded. Create an appeal.
                claimArbitrator.appeal{value: appealCost}(disputeData.externalDisputeID, arbitratorExtraData);
                disputeData.rounds.push();
                round.feeRewards = round.feeRewards.subCap(appealCost);
                round.sideFunded = Party.None;
            }
            fullyFunded = true;
        }
    }

    /** @dev Reimburse contributions if no disputes were raised in Arbitrator contract.
     *  If a dispute was raised, send the fee stake rewards and reimbursements proportionally to the contributions made to the winner of a dispute.
     *  @param _localDisputeID The index of the related dispute in the array of disputes.
     *  @param _beneficiary The address that made contributions.
     *  @param _round The round from which to withdraw.
     *  @param _ruling The ruling to withdraw from.
     *  @return reward The withdrawn reward value.
     */
    function withdrawFeesAndRewards(
        uint256 _localDisputeID,
        address payable _beneficiary,
        uint256 _round,
        uint256 _ruling
    ) public override returns (uint256 reward)  {
        DisputeData storage disputeData = disputes[_localDisputeID];
        ClaimData storage claimData = claimsData[disputeData.claimID];
        require(disputeData.resolved || claimData.status == Status.Resolved, 'Dispute or claim not resolved.');
        Round storage round = disputeData.rounds[_round];

        Party ruling = disputeData.ruling;
        if (_round == disputeData.rounds.length - 1) {
            // Reimburse if not enough fees were raised to appeal the ruling.
            reward = round.contributions[_beneficiary][_ruling];
        } else if (ruling == Party.None) {
            // Reimburse unspent fees proportionally if there is no winner or loser.
            uint256 totalFeesInRound = round.paidFees[uint256(Party.Claimant)] + round.paidFees[uint256(Party.Challenger)];
            reward = totalFeesInRound > 0 ? round.contributions[_beneficiary][_ruling] * round.feeRewards / totalFeesInRound : 0;
        } else if (_ruling == uint256(ruling)) {
            // Reward the winner.
            reward = round.paidFees[_ruling] != 0
                ? (round.contributions[_beneficiary][_ruling] * round.feeRewards) / round.paidFees[_ruling]
                : 0;

        }
        round.contributions[_beneficiary][_ruling] = 0;

        if (reward != 0) {
            _beneficiary.send(reward); // Deliberate use of send to prevent reverting fallback. It's the user's responsibility to accept ETH.
            emit Withdrawal(_localDisputeID, _round, _ruling, _beneficiary, reward);
        }
    }

    /** @dev Allows to withdraw any reimbursable fees or rewards after the dispute gets solved for multiple ruling options at once.
     *  Note that since this arbitrable contract only has 2 ruling options using this function isn't practical. It's implemented to match Dispute Resolver interface.
     *  @param _localDisputeID The index of the related dispute in the array of disputes.
     *  @param _contributor The address to withdraw rewards for.
     *  @param _roundNumber The number of the round caller wants to withdraw from.
     */
    function withdrawFeesAndRewardsForMultipleRulings(
        uint256 _localDisputeID,
        address payable _contributor,
        uint256 _roundNumber,
        uint256[] memory /* _contributedTo */
    ) public override {
        withdrawFeesAndRewards(_localDisputeID, _contributor, _roundNumber, uint256(Party.Claimant));
        withdrawFeesAndRewards(_localDisputeID, _contributor, _roundNumber, uint256(Party.Challenger));
    }

    /** @dev Allows to withdraw any rewards or reimbursable fees after the dispute gets resolved. For multiple rulings options and for all rounds at once.
     *  This function is O(n) where n is the number of rounds. This could exceed the gas limit, therefore this function should be used only as a utility and not be relied upon by other contracts.
     *  @param _localDisputeID The index of the related dispute in the array of disputes.
     *  @param _contributor The address to withdraw rewards for.
     *  @param _contributedTo Rulings that received contributions from contributor. Not used in this contract.
     */
    function withdrawFeesAndRewardsForAllRounds(
        uint256 _localDisputeID,
        address payable _contributor,
        uint256[] calldata _contributedTo
    ) external override {
        uint256 noOfRounds = disputes[_localDisputeID].rounds.length;
        for (uint256 roundNumber = 0; roundNumber < noOfRounds; roundNumber++) {
            withdrawFeesAndRewardsForMultipleRulings(_localDisputeID, _contributor, roundNumber, _contributedTo);
        }
    }

    /** @dev Submit a reference to evidence. EVENT.
     *  @param _localDisputeID The index of the related dispute in the array of disputes.
     *  @param _evidenceURI Link to evidence.
     */
    function submitEvidence(uint256 _localDisputeID, string calldata _evidenceURI) external override {
        emit Evidence(arbitrator, _localDisputeID, msg.sender, _evidenceURI);
    }

    /** @dev Submit a reference to evidence. EVENT.
     *  This function accept multiple evidence URIs at once. 
     *  @param _localDisputeID The index of the related dispute in the array of disputes.
     *  @param _evidences A list of links to evidences using its URIs.
     */
    function submitMultipleEvidence(uint256 _localDisputeID, string[] calldata _evidences) external {
        IArbitrator claimArbitrator = arbitrator;
        for (uint256 i = 0; i < _evidences.length; i++) {
            emit Evidence(claimArbitrator, _localDisputeID, msg.sender, _evidences[i]);
        }
    }

    /** @dev Give a ruling for a dispute. Can only be called by the arbitrator. TRUSTED.
     *  Accounts for the situation where the winner loses a case due to paying less appeal fees than expected.
     *  @param _disputeID ID of the dispute in the arbitrator contract.
     *  @param _ruling Ruling given by the arbitrator. Note that 0 is reserved for "Refused to arbitrate".
     */
    function rule(uint256 _disputeID, uint256 _ruling) external override {
        Party resultRuling = Party(_ruling);
        uint256 localDisputeID = externalIDtoLocalID[_disputeID];
        uint256 claimID = disputes[localDisputeID].claimID;
        ClaimData storage claimData = claimsData[claimID];

        require(claimData.status == Status.Disputed, 'Status must be Disputed.');
        require(_ruling <= RULING_OPTIONS, 'Invalid ruling option');
        require(address(arbitrator) == msg.sender, 'Only the arbitrator can execute');

        DisputeData storage disputeData = disputes[localDisputeID];
        Round storage round = disputeData.rounds[disputeData.rounds.length - 1];

        // The ruling is inverted if the loser paid its fees.
        if (round.sideFunded == Party.Claimant) // If one side paid its fees, the ruling is in its favor. Note that if the other side had also paid, an appeal would have been created.
            resultRuling = Party.Claimant;
        else if (round.sideFunded == Party.Challenger)
            resultRuling = Party.Challenger;

        emit Ruling(IArbitrator(msg.sender), _disputeID, uint256(resultRuling));
        executeRuling(claimID, claimData, disputeData, resultRuling);
    }

    // ************************ //
    // *       Getters        * //
    // ************************ //

    /** @dev Returns stake multipliers.
     *  @return _winnerStakeMultiplier Winners stake multiplier.
     *  @return _loserStakeMultiplier Losers stake multiplier.
     *  @return _loserAppealPeriodMultiplier Multiplier for calculating the duration of the appeal period for losing side. This contract uses default 1/2 of normal period.
     *  @return divisor Multiplier divisor in basis points.
     */
    function getMultipliers()
        external
        view
        override
        returns (
            uint256 _winnerStakeMultiplier,
            uint256 _loserStakeMultiplier,
            uint256 _loserAppealPeriodMultiplier,
            uint256 divisor
        )
    {
        return (winnerStakeMultiplier, loserStakeMultiplier, 5000, MULTIPLIER_DIVISOR);
    }

    /** @dev Returns number of possible ruling options.
     *  @return count The number of ruling options.
     */
    function numberOfRulingOptions(uint256 /* _localDisputeID */) external view override returns (uint256) {
        return RULING_OPTIONS;
    }

    /** @dev Get the number of rounds of a local disputes.
     *  @param _localDisputeID The index of the dispute in the array of disputes.
     *  @return The number of rounds of the dispute.
     */
    function getNumberOfRounds(uint256 _localDisputeID) external view returns (uint256) {
        return disputes[_localDisputeID].rounds.length;
    }

    /** @dev Get the contributions made by a party for a given round of a dispute.
     *  @param _localDisputeID The index of the dispute in the array of disputes.
     *  @param _round The round to query.
     *  @param _contributor The address of the contributor.
     *  @return contributions The contributions.
     */
    function getContributions(
        uint256 _localDisputeID,
        uint256 _round,
        address _contributor
    ) external view returns(uint256[3] memory contributions) {
        Round storage round = disputes[_localDisputeID].rounds[_round];
        contributions = round.contributions[_contributor];
    }

    /** @dev Get the information of a round of a dispute.
     *  @param _localDisputeID The index of the dispute in the array of disputes.
     *  @param _round The round to query.
     *  @return appealed Whether the round was appealed.
     *  @return paidFees Total fees paid in this round.
     *  @return sideFunded The party that paid the appeal fees first.
     *  @return feeRewards The amount of fees to use as rewards.
     */
    function getRoundInfo(uint256 _localDisputeID, uint256 _round)
        external
        view
        returns (
            bool appealed,
            uint256[3] memory paidFees,
            Party sideFunded,
            uint256 feeRewards
        )
    {
        DisputeData storage disputeData = disputes[_localDisputeID];
        Round storage round = disputeData.rounds[_round];
        return (
            _round != (disputeData.rounds.length - 1),
            round.paidFees,
            round.sideFunded,
            round.feeRewards
        );
    }

    /** @dev Get the required deposit for the claimant.
     *  @param _marketAddress The address of the market to define the token address.
     *  @param _amount The amount to convert into ETH.
     *  @return The deposit the claimant has to pay.
     */
    function getClaimantDeposit(address _marketAddress, uint256 _amount) public view returns (uint256) {
        address marketToken = IClaimableMarket(_marketAddress).getPoolAsset();
        address oracle = tokenToOracle[marketToken];
        require(oracle != address(0), 'The token is not supported.');
        // Use an oracle to perform the conversion.
        uint256 amountInETH = IOracle(oracle).consult(marketToken, _amount);
        uint256 arbitrationCost = arbitrator.arbitrationCost(arbitratorExtraData);
        uint256 proportionalDeposit = arbitrationCost.addCap((amountInETH.mulCap(claimantMultiplier)) / MULTIPLIER_DIVISOR);
        // Minimum deposit is paid in case when the amount is too low, to avoid spamming claims.
        return proportionalDeposit > minimumDeposit ? proportionalDeposit : minimumDeposit;
    }

    /** @dev Get the cost of appeal of the claims' dispute.
     *  @param _claimID The ID of the associated claim.
     *  @return appealCost to be paid. Note that this cost doesn't include crowdfunding fees.
     */
    function getAppealCost(uint256 _claimID) external view returns (uint256 appealCost) {
        ClaimData storage claimData = claimsData[_claimID];
        require(claimData.status == Status.Disputed, 'Status must be Disputed.');
        uint256 externalDisputeID = disputes[claimData.localDisputeID].externalDisputeID;
        appealCost = arbitrator.appealCost(externalDisputeID, arbitratorExtraData);
    }

    /** @dev Get the deposit required for challenging the claim.
     *  @param _claimID The ID of the associated claim.
     *  @return challengeCost to be paid.
     */
    function getChallengeCost(uint256 _claimID) external view returns (uint256 challengeCost) {
        ClaimData storage claimData = claimsData[_claimID];
        require(claimData.status == Status.Created, 'Status must be Created.');
        require(now - claimData.lastActionTime <= challengeTimeout, 'Time to challenge has passed.');
        challengeCost = arbitrator.arbitrationCost(arbitratorExtraData);
    }

    /** @dev Get the cost of making a claim.
     *  @param _marketAddress The address of the market to define the token address.
     *  @param _amount The claimed amount.
     *  @return deposit The fraction of the claimed amount converted into ETH.
     */
    function getClaimCost(address _marketAddress, uint256 _amount) external view returns (uint256 deposit) {
        deposit = getClaimantDeposit(_marketAddress, _amount);
    }

    /** @dev Returns the sum of withdrawable amount.
     *  This function is O(n) where n is the number of rounds. This could exceed the gas limit, 
     *  therefore this function should be used only as a utility and not be relied upon by other contracts.
     *  @param _localDisputeID The index of the related dispute in the array of disputes.
     *  @param _contributor The contributor for which to query.
     *  @return sum The total amount available to withdraw.
     */
    function getTotalWithdrawableAmount(
        uint256 _localDisputeID,
        address payable _contributor,
        uint256[] calldata /* _contributedTo */
    ) external view override returns (uint256 sum) {
        DisputeData storage disputeData = disputes[_localDisputeID];
        ClaimData storage claimData = claimsData[disputeData.claimID];
        require(disputeData.resolved || claimData.status == Status.Resolved, 'Dispute or claim not resolved.');
        Party ruling = disputeData.ruling;
        uint256 noOfRounds = disputeData.rounds.length;
        for (uint256 roundNumber = 0; roundNumber < noOfRounds; roundNumber++) {
            Round storage round = disputeData.rounds[roundNumber];
            if (roundNumber == disputeData.rounds.length - 1) {
                sum += round.contributions[_contributor][uint256(Party.Claimant)] + round.contributions[_contributor][uint256(Party.Challenger)];
            } else if (ruling == Party.None) {
                uint256 totalFeesInRound = round.paidFees[uint256(Party.Claimant)] + round.paidFees[uint256(Party.Challenger)];
                uint256 claimableFees = round.contributions[_contributor][uint256(Party.Claimant)] + round.contributions[_contributor][uint256(Party.Challenger)];
                sum += totalFeesInRound > 0 ? claimableFees * round.feeRewards / totalFeesInRound : 0;
            } else {
                sum += round.paidFees[uint256(ruling)] != 0
                    ? (round.contributions[_contributor][uint256(ruling)] * round.feeRewards) / round.paidFees[uint256(ruling)]
                    : 0;
            }
        }
    }

    /* Internal */

    /** @dev Execute the ruling of a dispute.
     *  @param _claimID The ID of the claim the dispute is related to.
     *  @param _claimData The data of the disputed claim.
     *  @param _disputeData The data of the dispute.
     *  @param _ruling Ruling given by the arbitrator. Note that 0 is reserved for "Refused to arbitrate".
     */
    function executeRuling(uint256 _claimID, ClaimData storage _claimData, DisputeData storage _disputeData, Party _ruling) internal {
        _disputeData.ruling = _ruling;
        _disputeData.resolved = true;
        uint256 localDisputeID = _claimData.localDisputeID;
        // Enforce the ruling by calling a specific function in Claims contract.
        if (_ruling == Party.Claimant) {
            _claimData.status = Status.Resolved;
            if (_claimData.amountReadjusted)
                claims.decideUserClaimChangeAmount(_claimData.marketAddress, _claimData.groupClaimId, _claimData.userClaimId, true, _claimData.amount);
            else
                claims.decideUserClaim(_claimData.marketAddress, _claimData.groupClaimId, _claimData.userClaimId, true);

            emit ClaimResolved(_claimID);
        // Give the claimant a chance to readjust the claimed amount or report the claim as unsuccessul if this chance was already used.
        } else {
            if (_claimData.amountReadjusted) {
                _claimData.status = Status.Resolved;
                claims.decideUserClaim(_claimData.marketAddress, _claimData.groupClaimId, _claimData.userClaimId, false);
                emit ClaimResolved(_claimID);
            } else {
                _claimData.status = Status.Readjustable;
                _claimData.lastActionTime = uint64(now);
            }
        }

        // Automatically withdraw from the 0 round for the party that is eligible for reimbursement/reward.
        if (_ruling == Party.None) {
            withdrawFeesAndRewards(localDisputeID, _claimData.claimant, 0, uint(Party.Claimant));
            withdrawFeesAndRewards(localDisputeID, _claimData.challenger, 0, uint(Party.Challenger));
        } else if (_ruling == Party.Claimant) {
            withdrawFeesAndRewards(localDisputeID, _claimData.claimant, 0, uint(Party.Claimant));
        } else {
            withdrawFeesAndRewards(localDisputeID, _claimData.challenger, 0, uint(Party.Challenger));
        }
    }

    /** @dev Make a fee contribution.
     *  @param _round The round to contribute.
     *  @param _side The side for which to contribute.
     *  @param _amount The amount contributed.
     *  @param _totalRequired The total amount required for this side.
     *  @return contribution The amount of appeal fees contributed.
     */
    function contribute(Round storage _round, Party _side, uint256 _amount, uint256 _totalRequired)
        internal
        returns (uint256 contribution)
    {
        // Take up to the amount necessary to fund the current round at the current costs.
        uint256 remainingETH; // Remaining ETH to send back.
        (contribution, remainingETH) = calculateContribution(_amount, _totalRequired.subCap(_round.paidFees[uint256(_side)]));
        _round.contributions[msg.sender][uint256(_side)] += contribution;
        _round.paidFees[uint256(_side)] += contribution;
        _round.feeRewards += contribution;

        // Reimburse leftover ETH.
        if (remainingETH > 0)
            msg.sender.send(remainingETH); // It's the user's responsibility to accept ETH.
    }

    /** @dev Return the contribution value and remainder from available ETH and required amount.
     *  @param _available The amount of ETH available for the contribution.
     *  @param _requiredAmount The amount of ETH required for the contribution.
     *  @return taken The amount of ETH taken.
     *  @return remainder The amount of ETH left from the contribution.
     */
    function calculateContribution(uint256 _available, uint256 _requiredAmount)
        internal
        pure
        returns(uint256 taken, uint256 remainder)
    {
        if (_requiredAmount > _available)
            return (_available, 0); // Take whatever is available, return 0 as leftover ETH.
        else
            return (_requiredAmount, _available - _requiredAmount);
    }
}

